var sorted = michaels.sort(function (x) { return Math.random() - 0.5; });

var bracketRounds = [];

var roundLevelCount = Math.log2(michaels.length);

var matchCount = (michaels.length) - 1;

var roundLevel = 0;

//initial first round
for (var i = 0; i < michaels.length; i += 2) {
    bracketRounds.push({
        members: [
            michaels[i],
            michaels[i + 1]
        ],
        level: roundLevel,
        winnerMovesTo: Math.floor(i / 4 + michaels.length / 2),
        levelOffsetIndex: i
    });
}

init();

var roundDisplay;
var roundTotalDisplay;

function init() {
    var buttonA = createMichaelButton(0);
    var buttonB = createMichaelButton(1);

    document.body.appendChild(buttonA);
    document.body.appendChild(createVersus());
    document.body.appendChild(buttonB);

    roundDisplay = document.getElementById("roundcounter--round");
    roundTotalDisplay = document.getElementById("roundcounter--total");

    roundTotalDisplay.textContent = matchCount;

    createNextBracketRound(buttonA, buttonB);
}



function createNextBracketRound(buttonA, buttonB, index) {
    index = (index || 0);

    var round = bracketRounds[index];

    roundDisplay.textContent = index + 1;

    updateCosmeticFlavourText(round);

    populateMichaelButton(buttonA, round.members[0]);
    populateMichaelButton(buttonB, round.members[1]);

    //when a button is clicked, then record the choice and go to next match
    buttonA.onclick = buttonB.onclick = function (event) {
        var memberIndex = parseInt(seekUpToButton(event.target).getAttribute("data-i"));
        var winner = round.members[memberIndex];

        var nextRoundForWinner = initRoundIfNotExists(round.winnerMovesTo, round.level + 1);

        nextRoundForWinner.members.push(winner);
        if (nextRoundForWinner.winnerMovesTo != bracketRounds.indexOf(nextRoundForWinner)) createNextBracketRound(buttonA, buttonB, index + 1);
        else displayResults();
    }
}

function displayResults() {
    var prevWinners = [];
    var results = document.createElement("ol");
    var resultData = {};
    results.classList.add("results");

    var levelNames = ["Contestants", "Semi-Semi-Finalists", "Semi-Finalists", "Finalists", "Runner-Up", "Winner"]
    
    for(var j = roundLevelCount; j >= 0; j--) {
        var levelRounds = bracketRounds.filter(function(x) { return x.level == j });

        var levelResultsParent = document.createElement("li");
        var levelResultsHead = document.createElement("h2");
        var levelResults = document.createElement("ul");

        levelResultsHead.textContent = levelNames[j];

        levelResultsParent.appendChild(levelResultsHead);
        levelResultsParent.appendChild(levelResults);
        results.appendChild(levelResultsParent);

        for(var k = 0; k < levelRounds.length; k++) {
            var round = levelRounds[k];
            var added = 0;

            for(var h = 0; h < round.members.length; h++) {
                var member = round.members[h];
                if(prevWinners.indexOf(member) == -1) {
                    added++;
                    prevWinners.push(member);
                    resultData[member.name] = j;
                    var card = createWinnerCard(member, round.level);
                    var cardLiParent = document.createElement("li");

                    cardLiParent.classList.add("level" + round.level);
                    cardLiParent.appendChild(card);
                    levelResults.appendChild(cardLiParent);
                }
            }

            if(added) continue;
        }
    }

    submitResults(resultData);

    document.body.classList.add("results");
    clearChildren(document.body);
    document.body.appendChild(createResultsControls());
    document.body.appendChild(results);
}

function createResultsControls() {
    var parent = document.createElement("div");
        heading = document.createElement("h1");
        viewPublic = document.createElement("a");

    heading.textContent = "Results";

    viewPublic.textContent = "View Global Results";
    viewPublic.href = "#global";
    viewPublic.onclick = displayPublicResults;

    parent.classList.add("results-controls");

    parent.appendChild(heading);
    parent.appendChild(viewPublic);

    return parent;
}

function submitResults(resultData) {
    console.log(resultData);
    var xhr = new XMLHttpRequest();

    xhr.open("POST", "/api/submit");

    xhr.setRequestHeader("Content-Type", "application/json");

    xhr.send(JSON.stringify(resultData));
}

function displayPublicResults() {
    var modal = document.createElement("dialog");
    modal.onclick = function(event) {
        console.log(event);
        if(event.target != modal) return;
        if(modal.parentElement) {
            modal.parentElement.classList.remove("has-modal");
            modal.parentElement.removeChild(modal);
        }
    }
    modal.setAttribute("open", "true");

    var modalInner = document.createElement("div");
    modalInner.onclick = function(event) {
        event.stopPropagation();
    }
    modal.appendChild(modalInner);

    var header = document.createElement("h2");
    header.textContent = "Global Results";
    modalInner.appendChild(header);

    var table = document.createElement("table");

    getPublicResults(function(data) {
        var dataEntries = Object.entries(data);

        var max = 0;
        for(var i = 0; i < dataEntries.length; i++) max = Math.max(max, dataEntries[i][1]);

        dataEntries = dataEntries.sort(function(a, b) { return b[1] - a[1]; })

        for(var i = 0; i < dataEntries.length; i++) {
            var tr = document.createElement("tr");

            var name = document.createElement("th");
            name.textContent = dataEntries[i][0];

            var data = document.createElement("td");
            var dataNum = document.createElement("span");
            dataNum.textContent = dataEntries[i][1];

            var dataGraph = document.createElement("b");
            dataGraph.setAttribute("aria-hidden", "true");
            dataGraph.style.width =  (dataEntries[i][1] / max * 100) + "%";

            data.appendChild(dataNum);
            data.appendChild(dataGraph);

            tr.appendChild(name);
            tr.appendChild(data);

            table.appendChild(tr);
        }

        modalInner.appendChild(table);
    });

    document.body.classList.add("has-modal");
    document.body.appendChild(modal);
}

function getPublicResults(cb) {
    var xhr = new XMLHttpRequest();

    xhr.open("POST", "/api/get-global");

    xhr.onload = function() {
        cb(JSON.parse(xhr.responseText));
    } 

    xhr.send();
}

function createWinnerCard(michael, level) {
    var card = document.createElement("div");
    var highlightHue = genHueName(michael.name.split(" ")[1]);

    //only include picture for winner, runner-up, and finalists
    if(roundLevelCount - level < 3) {
        var image = document.createElement("img");
        populateMichaelImg(image, michael);
        card.appendChild(image);
    }

    var michaelName = document.createElement("h3");

    //only semi-finalist to winners get colored name
    if(roundLevelCount - level < 4) {
        michaelName.innerHTML = emphasize(michael.name, highlightHue);
    } else {
        michaelName.textContent = michael.name;
    }

    card.appendChild(michaelName);
    return card;
}

function clearChildren(elem) {
    while(elem.children[0]) elem.removeChild(elem.children[0]);
}

function updateCosmeticFlavourText(round) {
    var termForGame = document.getElementById("roundcounter--termforgame");
    var cta = document.getElementById("cta");

    var termsForGame = ["Match", "Contest", "Gladatorial Fight", "Traffic Jam", "Creative Difference", "BabyFights", "Wrestling Match",
        "Rock Paper Scissors", "Round", "Duel", "Holy War", "Oh Boy Another One"];
    
    termForGame.textContent = termsForGame[
        Math.floor(rand(63 + round.level*michaels.length + round.levelOffsetIndex)() * termsForGame.length * (round.level / roundLevelCount * 2)) 
    ];

    var ctas = [["Select the Winner!"],
                ["Who will win?", "Let's go team!", "Who will move on to the Elite 8?"],
                ["It's getting tense now!", "This one's for all the marbles!", "Who's the winner?", "Wow, a tense one, folks!", "Wow, a close one!"],
                ["Who will move into the finals?", "Select the winner!"],
                ["The final battle!"]
            ];

    cta.textContent = ctas[round.level][ Math.floor( Math.random()*ctas[round.level].length ) ];
    cta.className = "level" + (round.level+1);

}

function initRoundIfNotExists(roundIndex, level) {
    if (!bracketRounds[roundIndex]) {
        if (roundIndex > bracketRounds.length - 1) bracketRounds.length = roundIndex;

        var levelFloor = michaels.length - Math.pow(2, roundLevelCount - level);
        var nextLevelFloor = michaels.length - Math.pow(2, roundLevelCount - (level + 1));
        var levelHeight = nextLevelFloor - levelFloor;
        var levelOffsetIndex = roundIndex - levelFloor;

        var winnerMovesTo = levelFloor + Math.floor(levelOffsetIndex / 2 + levelHeight);

        bracketRounds[roundIndex] = {
            members: [],
            level: level,
            winnerMovesTo: winnerMovesTo,
            levelOffsetIndex: levelOffsetIndex
        }
    }

    return bracketRounds[roundIndex];
}

function createMichaelButton(i) {
    var button = document.createElement("button"),
        heading = document.createElement("h2"),
        description = document.createElement("p"),
        thumbnail = document.createElement("img");

    button.appendChild(thumbnail);
    button.appendChild(heading);
    button.appendChild(description);

    button.setAttribute("data-i", i);
    return button;
}

function populateMichaelButton(button, michael) {
    populateMichaelImg(button.children[0], michael);    

    var hue = genHueName(michael.name.split(" ")[1]);
    button.style.setProperty("--highlight-color", `hsl(${hue}, 45%, 28%)`);

    button.children[1].innerHTML = emphasize(michael.name, hue);

    button.children[2].textContent = michael.info.extract;
}

function populateMichaelImg(img, michael) {
    img.src = michael.info.thumbnail.source;
    img.onerror = function () {
        img.src = michael.info.originalimage.source;
        img.onerror = null;
    }
}

function emphasize(name, hue) {
    var names = name.split(" ");

    return `${names[0]} <em style="color: hsl(${hue}, 45%, 28%);">${names.slice(1).join(" ")}</em>`;
}

function createVersus() {
    var sp = document.createElement("span");
    sp.classList.add("versus");
    sp.textContent = "Versus";
    return sp;
}

function genHueName(name) {
    var nameseed = parseInt(btoa(name).toLowerCase(), 36);
    return rand(nameseed)() * 360;
}

function rand(seed) {
    return function () {
        var x = Math.sin(seed++) * 10000;
        return (x - Math.floor(x));
    }
}

function factorial(x) {
    x = x - x % 1;
    var a = 0;
    for (; x > 0; x--) a += x;
    return a;
}

function seekUpToButton(elem) {
    while (elem.parentElement && elem.tagName != "BUTTON") elem = elem.parentElement;
    return elem;
}